import './App.css';
import tinycolor from 'tinycolor2';
import React, { Component } from 'react';
import { BlockPicker } from 'react-color';
import Color from './components/Color';

class App extends Component {
  state = {
    primary: '#794CFF',
    gradientDark: '#480BFF',
    howExpanded: false,
    copied: false
  }

  handleExpandToggle = () => {
    this.setState({
      ...this.state, 
      howExpanded: !this.state.howExpanded
    })
  }

  handleChangeComplete = (newPrimary) => {
    const newGradientDark = tinycolor(newPrimary.hex).darken(12.8).toString();;
    this.setState({
      ...this.state,
      primary: newPrimary,
      gradientDark: newGradientDark,
      copied: false
    });
  }

  handleCopyText = (e) => {
    e.preventDefault();
    navigator.clipboard.writeText(this.state.gradientDark);
    this.setState({ ...this.state, copied: true });
  };


  render() {
    const { primary, gradientDark, howExpanded, copied } = this.state;
    return (
      <div className="App">
        <header className="App-header">
          {/* <img src={logo} className="App-logo" alt="logo" /> */}
          <h1>
            Brand Colors Generator  <span role="img" aria-label="artist">ğŸ§‘ğŸ»â€ğŸ¨</span>
          </h1>
          <p>A tool for generating all branded colors required in the FFAI app.</p>
          {/* <a className='App-link' onClick={this.handleExpandToggle}><small>How to Use</small></a>
          {howExpanded &&
            <div className='howTextBlock'>
            <p>1) In the color picker below, select a color as your brand primary color.</p>
            <p>2) Copy the auto-generated branded colors by clicking the "Copy" button!</p>
            </div>} */}
          <hr style={{width: '80vw'}}/>
        </header>
        <main style={{width: '80%'}}>
          <div style={{ display: 'flex', width: '100%' }}></div> 
          <form>
            <label>1) Select or input your <code>brand-primary-color</code></label>
              <BlockPicker
                  color={primary}
                  colors={['#794CFF', '#005DAA', '#BF9E66', '#3498DB', '#ED0722', '#54b848', '#D9E3F0', '#F47373', '#697689', '#37D67A', '#2CCCE4', '#555555', '#dce775', '#ff8a65', '#ba68c8' ]}
                  triangle='hide'
                  onChangeComplete={this.handleChangeComplete}
              />
          </form>

          <form>
            <label>2) Preview and copy your <code>gradient-dark</code><Color color={gradientDark} /></label>
            <input type="text" id="gradient-dark" name="gradient-dark" value={gradientDark} />
            <input type="submit" value={!copied ? 'Copy' : 'Copied!'} onClick={this.handleCopyText}/> 
          </form>
          <p>3) You're all set! ğŸ¥³ See you again next time.</p>
        </main>
        <footer>
        <hr style={{width: '80vw'}}/>
        </footer>
      </div>
    );
  }

}

export default App;
