import './App.css';
import tinycolor from 'tinycolor2';
import React, { Component } from 'react';

class App extends Component {
  state = {
    primary: '#794CFF',
    gradientDark: '#480BFF'
  }

  onChange = (e) => {
    this.setState({ ...this.state, primary: e.target.value });
  }

  handleGenerate = (e) => {
    const { primary } = this.state;
    e.preventDefault();
    const gradientDark = tinycolor(primary).getBrightness();
    console.log(gradientDark);
  }

  render() {
    const { primary, gradientDark } = this.state;
    return (
      <div className="App">
        <header className="App-header">
          {/* <img src={logo} className="App-logo" alt="logo" /> */}
          <h1>
            FFAI Brand Colors Generator  <span role="img" aria-label="artist">ğŸ§‘ğŸ»â€ğŸ¨</span>
          </h1>
          <p>A tool for generating all branded colors required in the FutureFit AI app.</p>
        </header>
        <main style={{textAlign: 'left'}}>
          <form>
            <label htmlFor="brand-primary">Brand primary color:</label>
            <input type="text" id="brand-primary" name="brand-primary" value={primary} onChange={this.onChange}/>
            <input type="submit" value="Generate" onClick={this.handleGenerate}/>
          </form>
          <form>
          <label>Gradient dark:</label>
            <input type="text" id="gradient-dark" name="gradient-dark" value={gradientDark} />
            <input type="submit" value="Copy" />
          </form>
        </main>
      </div>
    );
  }

}

export default App;
