import './App.css';
import tinycolor from 'tinycolor2';
import React, { Component } from 'react';
import { BlockPicker, CirclePicker } from 'react-color';

class App extends Component {
  state = {
    primary: '#794CFF',
    gradientDark: '#480BFF'
  }

  onChange = (e) => {
    this.setState({ ...this.state, primary: e.target.value });
  }

  handleChangeComplete = (newPrimary) => {
    const newGradientDark = tinycolor(newPrimary.hex).darken(12.8).toString();;
    this.setState({
      ...this.state,
      primary: newPrimary,
      gradientDark: newGradientDark
    });
  }

  render() {
    const { primary, gradientDark } = this.state;
    return (
      <div className="App">
        <header className="App-header">
          {/* <img src={logo} className="App-logo" alt="logo" /> */}
          <h1>
            FFAI Brand Colors Generator  <span role="img" aria-label="artist">🧑🏻‍🎨</span>
          </h1>
          <p>A tool for generating all branded colors required in the FutureFit AI app.</p>
        </header>
        <main style={{textAlign: 'center'}}>
            <BlockPicker
              color={primary}
              colors={['#794CFF', '#005DAA', '#BF9E66', '#3498DB','#D9E3F0', '#F47373', '#697689', '#37D67A', '#2CCCE4', '#555555', '#dce775', '#ff8a65', '#ba68c8' ]}
              triangle='hide'
              onChangeComplete={this.handleChangeComplete}
            />
          <form>
          <label>Gradient dark:</label>
            <input type="text" id="gradient-dark" name="gradient-dark" value={gradientDark} />
            <input type="submit" value="Copy" /> <div style={{ backgroundColor: gradientDark, width: '20px', height: '20px', borderRadius: '50%'}}></div>
          </form>
        </main>
      </div>
    );
  }

}

export default App;
